#include <bits/stdc++.h>
using namespace std;

/*
idea is to used bfs to traverse min path
then record all path
then used dfs to record all min path
*/

// graph data structure

struct Node
{
  vector<Node*> next;
  string val;
};

struct hashFunction
{
  pair<string, Node*> operator()(const pair<string, Node*> &x) const
  {
    return make_pair(x.first, x.second);
  }
};

class Solution
{
  public:

    void traverse(Node *curr, string end, vector<vector < string>> &result, vector< string > temp, int &mini)
    {
      if (curr->val == end)
      {
        temp.push_back(curr->val);
        if (temp.size() < mini)
        {
          result.clear();
          mini = temp.size();
          reverse(temp.begin(), temp.end());
          result.push_back(temp);
        }
        else if (temp.size() == mini)
        {
          reverse(temp.begin(), temp.end());
          result.push_back(temp);
        }

        temp.pop_back();
        return;
      }

      for (int i = 0; i < curr->next.size(); i++)
      {
        temp.push_back(curr->val);
        traverse(curr->next[i], end, result, temp, mini);
        temp.pop_back();
      }

      return;
    }

  vector<vector < string>> findLadders(string beginWord, string endWord, vector<string> &wordList)
  {
   	// set to store unique string and fast retriva;
    wordList.push_back(beginWord);
    unordered_map<string, Node*> dict;
   	//unordered_set<string> hash(wordList.begin(),wordList.end());
    unordered_map<string, int> hash;

    for (auto &x: wordList)
    {
      if (!dict.count(x))
      {
        Node *temp = new Node;
        temp->val = x;
        dict.insert({ x, temp });
        hash.insert({ x, -1 });
      }
    }

    if (hash.find(endWord) == hash.end()) return {};

   	//
    hash[beginWord] = 0;

   	// queue to travese in bfs
    queue<string> que;

   	//push start in que
    que.push(beginWord);

    int level = 0;

    while (!que.empty())
    {
      level++;
      string curr = que.front();
      que.pop();
     	// size of string
      if (curr == endWord) break;
      vector<string> remove;
      for (int i = 0; i < curr.size(); i++)
      {
        auto original = curr;
        for (auto x = 'a'; x <= 'z'; x++)
        {
         	// replace string
          curr[i] = x;
          if (hash.count(curr))
          {
            if (hash[curr] == -1)
            {
              que.push(curr);
              dict[curr]->next.push_back(dict[original]);
              hash[curr] = level;
            }
            else if (hash[curr] > hash[original])
            {
              dict[curr]->next.push_back(dict[original]);
            }
          }
        }

        curr = original;
      }
    }

   	// queue end;
    vector<vector < string>> result;
    vector<string> temp;
    int mini = INT_MAX;
    traverse(dict[endWord], beginWord, result, temp, mini);
    return result;
  }
};

void operator<<(ostream &OUT, vector<vector < string>> list)
{
  for (auto &x: list)
  {
    for (auto &y: x)
    {
      OUT << y << " ";
    }

    cout << "\n";
  }

  return;
}

